import React from 'react';
import { Nav } from 'react-bootstrap';

const Jobs = () => {

  let houston = ["basic", "girls", "tofuckthem"];
  const [city, setCity] = React.useState("Houston");
  const [data, setData] = React.useState(houston);

  const handleSelect = (activeKey) => {
    if(activeKey === "shiraz") {
      setData(["one", "two", "three"]);
      setCity(activeKey);
    } else {
      setData([1, 2,3])
      setCity(activeKey);
    }
  }


  return (
    <div className="jobs my-5">
      <div className="container">
       <div className="tab">
       <Nav className="justify-content-center"
             activeKey={city}
             variant="tabs"
             onSelect={handleSelect}>
          <Nav.Item>
            <Nav.Link eventKey="Houston">Houston</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="netherland">Netherland</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="tallinn">
            Tallinn
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="shiraz">
              Shiraz
            </Nav.Link>
          </Nav.Item>
        </Nav>
       </div>
       <div className="city">
        {data.map((i,idx) => (
          <div key={idx}>
            <h2>{i}</h2>
          </div>
        ))}
       </div>
      </div>
    </div>
  );
}

export default Jobs;
