import React from 'react';
import { FaGamepad, FaBook } from 'react-icons/fa';
import { GiWatch, GiSofa } from 'react-icons/gi';
import { AiOutlineHome } from 'react-icons/ai';
import { IoIosLaptop } from 'react-icons/io';

const Benefits = () => {
  return (
    <div className="benefits mt-5 pt-5 position-relative">
      <div className="container d-flex flex-column justify-content-around">
        <div className="benefits_text">
          <h2>working with bteks comes with great perks</h2>
          <p>Being a developer is not only about coding. Itâ€™s about evolving your passion.</p>
        </div>
        <div className="benefits_option d-flex justify-content-between">
          {items.map((item, idx)=> {
            return (
              <div className="item">
                <span>{item.icon}</span>
                <h5>{item.title}</h5>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Benefits;


const items = [
  {icon: <GiWatch/> ,title: "Flexible Hours"},
  {icon: <IoIosLaptop />, title: "Pro Tools"},
  {icon: <FaGamepad/>, title: "Gaming"},
  {icon: <AiOutlineHome/>, title: "Remote Friendly"},
  {icon: <GiSofa />, title: "Cozy Studio"},
  {icon: <FaBook/>, title: "Training Material"},
]
