import React from 'react';
import { Nav } from 'react-bootstrap';

const Jobs = () => {
const [city, setCity] = React.useState("Houston")

  const handleSelect = (activeKey) => {
    setCity(activeKey);
    if(city === "shiraz") {
      var data = ["one", "two", "three"]
    } else {
      var data = [1, 2,3]
    }
  }


  return (
    <div className="jobs my-5">
      <div className="container">
       <div className="tab">
       <Nav className="justify-content-center"
             activeKey="Houston"
             variant="tabs"
             onSelect={handleSelect}>
          <Nav.Item>
            <Nav.Link eventKey="Houston">Houston</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="netherland">Netherland</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="tallinn">
            Tallinn
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="shiraz">
              Shiraz
            </Nav.Link>
          </Nav.Item>
        </Nav>
       </div>
       <div className="city">
        {data.map(i => (
          <div>
            <h2>{i}</h2>
          </div>
        ))}
       </div>
      </div>
    </div>
  );
}

export default Jobs;
