import React from 'react';
import { Nav } from 'react-bootstrap';
import hstn from '../images/houston.jpeg';
import amsterdam from '../images/amsterdam.jpg';
import tallinn from '../images/tallinn.jpg';
import shiraz from '../images/shiraz.jpg';

const Jobs = () => {

  let houston = {img: hstn, jobs: ["Senior Full-Stack", "Data Science"]};
  const [city, setCity] = React.useState("Houston");
  const [data, setData] = React.useState(houston);

  const handleSelect = (activeKey) => {
    if(activeKey === "shiraz") {
      setData({img: shiraz, jobs: ["ui/ux designer"]});
      setCity(activeKey);
    } else {
      setData([1, 2,3])
      setCity(activeKey);
    }
  }


  return (
    <div className="jobs my-5">
      <div className="container">
       <div className="tab">
       <Nav className="justify-content-center"
             activeKey={city}
             variant="tabs"
             onSelect={handleSelect}>
          <Nav.Item>
            <Nav.Link eventKey="Houston">Houston</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="netherland">Netherland</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="tallinn">
            Tallinn
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="shiraz">
              Shiraz
            </Nav.Link>
          </Nav.Item>
        </Nav>
       </div>
       <div className="city">
        {data.map((i,idx) => (
          <div key={idx}>
            <ul>
              {i.jobs.map(j => (
                <li>{j}</li>
              ))}
            </ul>
          </div>
        ))}
       </div>
      </div>
    </div>
  );
}

export default Jobs;
